mapper.select:
SELECT MY_COUNT AS M_MY_COUNT
FROM CHAL_MONTH_MEM
WHERE MEM_NO = #{ no } AND CHAL_NO = #{ chalNo }

mapper.update:
UPDATE CHAL_REPLY
SET CONTENT = #{ content }
WHERE REPLY_NO = #{ replyNo }

UPDATE CHAL_NESTED_REPLY
SET CONTENT = #{ content }
WHERE NESTED_REPLY_NO = #{ nestedReplyNo }

mapper.select:
SELECT *
FROM CHAL_NESTED_REPLY
WHERE NESTED_REPLY_NO = #{ nestedReplyNo }
