UPDATE PRODUCT
SET PRO_STOCK = (SELECT PRO_STOCK FROM PRODUCT WHERE PRO_NO = #{proNo}) - #{qty},
    PRO_SOLD = (SELECT PRO_SOLD FROM PRODUCT WHERE PRO_NO = #{proNo}) + #{qty}
WHERE PRO_NO = #{proNo}

